import React, { Component  } from 'react';
import './Hello.css'; 

class Hello extends Component {
    render() {
        return (
        <div className = 'f1 tc'>  
        < h1>Hello World</h1>
        <p>Welcome to React</p>
        </div>
        );
    }
}
//f1 tc ==> tachyons (check website for more)
 //why className instead of class? ==> jsx - lets you write HTML type syntax in your JS file
 //But wb seperation of concerns? Well React's approach is to have the html/style for each component
 //and let that component live in it's own little universe and you can make your final
 //project by adding these components together
 //thus React changes the sep of concerns paradigm  

 //note: the h1 and p is not real HTML
 //kinda like the virtual DOM and react looking at the actual
